<?php
    
    if (isset($_POST['submitSearch'])){
        //get current state
        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/get_state');
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    
        function removeBOM($data) {
            if (0 === strpos(bin2hex($data), 'efbbbf')) {
               return substr($data, 3);
            }
        }
        
        $result = curl_exec($ch);
        curl_close($ch);
        $result = json_decode(removeBOM($result));
        print '<pre>';
        print_r($result);
        print '</pre>';
        echo($result);  

        if($result['state'] === 1){
            //we need to switch states
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/web_chat');
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, "{\"input_text\":\"ask\", \"key\":\"B670F7AB38\"}");
            curl_setopt($ch, CURLOPT_POST, 1);
        
            $headers = array();
            $headers[] = 'Content-Type: application/json';
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        
            $result = curl_exec($ch);

            curl_close ($ch);
        }
        $search = $_POST['search'];
        $input = "{\"input_text\":\"".$search."\", \"key\":\"B670F7AB38\"}";

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/web_chat');
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $input);
        curl_setopt($ch, CURLOPT_POST, 1);
    
        $headers = array();
        $headers[] = 'Content-Type: application/json';
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
    
        $result = curl_exec($ch);

        curl_close ($ch);
        
    }
    if (isset($_POST['submitEntry'])){
        //get current state
        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/get_state');
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        
        function removeBOM($data) {
            if (0 === strpos(bin2hex($data), 'efbbbf')) {
               return substr($data, 3);
            }
        }
        
        $result = curl_exec($ch);
        curl_close($ch);
        $result = json_decode(removeBOM($result));
        print '<pre>';
        print_r($result);
        print '</pre>';
        echo($result);     
        
        if($result['state'] === 2){
            //we need to switch states
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/web_chat');
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, "{\"input_text\":\"add\", \"key\":\"B670F7AB38\"}");
            curl_setopt($ch, CURLOPT_POST, 1);
        
            $headers = array();
            $headers[] = 'Content-Type: application/json';
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        
            $result = curl_exec($ch);

            curl_close ($ch);
            
        }
        $newEntry = $_POST['newEntry'];
        $input = "{\"input_text\":\"".$newEntry."\", \"key\":\"B670F7AB38\"}";

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, 'http://ec2-18-234-167-118.compute-1.amazonaws.com/web_chat');
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $input);
        curl_setopt($ch, CURLOPT_POST, 1);
    
        $headers = array();
        $headers[] = 'Content-Type: application/json';
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
    
        $result = curl_exec($ch);

        curl_close ($ch);

    }
?>
